import React from "react";
import { Card } from "./Card.styled";
import { ImageCard } from "./ImageCard.styled";
import { UiContext } from "../../App";
import { useContext } from "react";
const DropDown = () => {
  const Ui = useContext(UiContext);
  const modeProps = [
    { id: 1, src: Ui.lightModeIcon },
    { id: 2, src: Ui.darkModeIcon },
  ];
  const rendered = modeProps.map((item) => {
    return (
      <ImageCard
        bd={"2px solid #555"}
        br={"0.3srem"}
        height={"2rem"}
        src={item.src}
      />
    );
  });
  return (
    <>
      <Card
        ps={"absolute"}
        pslf={"32rem"}
        pstp={"4rem"}
        dp={"grid"}
        gridC={"auto auto"}
        gridjc={"space-around"}
        pd={"1rem"}
        zindex={"200"}
        height={"4rem"}
        width={"10rem"}
        bg={"white"}
        br={"0.5rem"}
        bs={"0.1px 0.1px 15px rgb(210, 210, 210)"}
      >
        {rendered}
      </Card>
    </>
  );
};

export default DropDown;
