import React from "react";
import { Card } from "./Card.styled";
import { ImageCard } from "./ImageCard.styled";
import { UiContext } from "../../App";
import { useContext } from "react";
const DropDown = () => {
  const Ui = useContext(UiContext);
  return (
    <>
      <Card
        ps={"absolute"}
        pslf={"28rem"}
        pstp={"4rem"}
        zindex={"200"}
        height={"5rem"}
        width={"10rem"}
        bg={"white"}
        br={"0.5rem"}
        bs={"0.1px 0.1px 15px rgb(210, 210, 210)"}
      >
        <Card
          dp={"grid"}
          mg={"1rem"}
          gridC={"auto auto"}
          gridjc={"space-around"}
          height={"2rem"}
          width={"8rem"}
          bd={"1px solid red"}
        >
          <ImageCard src={Ui.lightModeIcon} />
          <ImageCard src={Ui.darkModeIcon} />
        </Card>
      </Card>
    </>
  );
};

export default DropDown;
