import React from "react";
import styled from "styled-components";
import NavCardDropDown from "../../../NAVBAR/Components/NavCardDropDown";
// IMAGES
import Calender from "../../images/icon-calendar.svg";
import TodoList from "../../images/icon-todo.svg";
import Reminder from "../../images/icon-reminders.svg";
import Planning from "../../images/icon-planning.svg";

const Container = (props) => {
  const Container = styled.div`
    background-color: white;
    position: relative;
    left: 35%;
    width: 65%;
    height: 100%;
  `;
  const Image = styled.img`
    float: right;
    margin: 1.5rem;
    display: block;
    border: 1px solid black;
  `;

  const NavBarItems = [
    {
      content: "snap",
      snap: "bb",
    },
    {
      drop1: 1,
      content: "Features",
      icon: ArrowUp,
      activeIcon: ArrowDown,
      dropdown: [
        {
          text: "TodoList",
          imgsrc: TodoList,
        },
        {
          text: "Calender",
          imgsrc: Calender,
        },
        {
          text: "Reminder",
          imgsrc: Reminder,
        },
        {
          text: "Planning",
          imgsrc: Planning,
        },
      ],
    },
    {
      drop2: 2,
      content: "Company",
      icon: ArrowUp,
      activeIcon: ArrowDown,
      dropdown: [
        {
          text: "History",
        },
        {
          text: "Our Team",
        },
        {
          text: "Blog",
        },
      ],
    },
    {
      content: "Careers",
    },
    {
      content: "About",
    },
    {
      content: ".",
    },
    {
      content: "Login",
    },
    {
      content: "Register",
      button: " e",
    },
  ];
  const renderedItems = NavBarItems.map((items) => {
    return (
      <div>
        <NavCardDropDown senditems={items}></NavCardDropDown>
      </div>
    );
  });

  return (
    <>
      <Container>
        <Image src={props.CloseBtn} />
        <NavCardDropDown />
      </Container>
    </>
  );
};

export default Container;
