import "./App.css";
import { useState } from "react";
import { Card } from "./components/UI/Card.styled";
import DropDown from "./components/UI/DropDown";
import { createContext } from "react";
import React from "react";
import HomePage from "./components/HOMEPAGE/HomePage";

import userIcon from "./images/user-regular.svg";
import moonIcon from "./images/moon-solid.svg";
import download from "./images/download.svg";
import download2 from "./images/download2.svg";

import toggleOn from "./images/toggleOn.svg";
import toggleOff from "./images/toggleOff.svg";
import downCaret from "./images/downCaret.svg";

import Engineer from "./images/engineer.svg";

export const UiContext = createContext();
const App = () => {
  // const [state, setState] = useState(0);
  // setTimeout(() => {
  //   setState(state + 1);
  //   console.log(state);
  // }, 2000);
  const [mode, setMode] = useState(false);

  const UI = {
    userIcon: userIcon,
    moonIcon: moonIcon,
    Engineer: Engineer,
    download: download,
    download2: download2,
    toggleOn: toggleOn,
    toggleOff: toggleOff,
    downCaret: downCaret,
  };
  const colors = {
    bgColor: mode ? "black" : "white",
    darkGrey: "#353535",
    lightGrey: " #5b5b5b",
    lighterGrey: "#484848",
    textWhite: "#ffffff",
    darkModeColor: "#0c0c0d",
    mainColor: "#ffc200",
    borderColor: "#f2f2f1",
    mainYellow: "#ffc200",
    buttonColor: "#474559",
  };
  const fontSizes = {
    NavBar: "0.7rem",
    textNormal: "0.7rem",
    textButton: "0.6rem",
    textHeader: "1.5rem",
  };
  const [UiState, setUiState] = useState(UI);

  return (
    <UiContext.Provider value={UiState}>
      <DropDown />
      {/* <HomePage fontSizes={fontSizes} UI={UI} colors={colors} /> */}
    </UiContext.Provider>
  );
};

export default App;
