import React from "react";
import { Card } from "../../UI/Card.styled";
import { ImageCard } from "../../UI/ImageCard.styled";
import { Button } from "./../../UI/Button.styled";

const SectionOne = ({ fontSizes, UI, colors }) => {
  const job = "Oje Paul";
  return (
    <>
      <Card
        mg={"4rem 3rem"}
        height={"12rem"}
        dp={"grid"}
        gridC={"60% 40%"}
        bd={"px solid red"}
      >
        <Card bd={"0px solid red"}>
          <Card
            height={"80%"}
            width={"90%"}
            mg={"5% 0 0 0"}
            // bd={"1px solid red"}
          >
            <Card cl={colors.lightGrey} fw={"700"} fs={"0.6rem"}>
              Welcome to my portfolio website!
            </Card>
            <Card mg={"1rem 0 0 0"} fw={"700"} fs={"1.5rem"}>
              {`Hey Folks,i'm ${job}`}
            </Card>

            <Card
              cl={colors.lightGrey}
              mg={"1rem 0 0 0"}
              fs={fontSizes.textNormal}
            >
              Building a successful product is a challenge. I am highly
              energetic in user experience design, interfaces and web
              development.
            </Card>
            <Button
              mg={"1rem 0 0 0"}
              bd={"none"}
              br={"2rem"}
              height={"1.5rem"}
              width={"8rem"}
            >
              <Card
                // bd={"1px solid red"}
                mg={"0 10%"}
                width={"80%"}
                height={"50%"}
              >
                <ImageCard
                  src={UI.download}
                  width={"0.6rem"}
                  height={"0.6rem"}
                  mg={"0 0.2rem -0.1rem 0"}
                />
                <Card
                  font={"Maven Pro, sans-serif"}
                  dp={"inline"}
                  fs={"0.6rem"}
                >
                  Download Resume
                </Card>
              </Card>
            </Button>
          </Card>
        </Card>
        <Card bd={"0px solid red"}>
          <ImageCard height={"100%"} width={"100%"} src={UI.Engineer} />
        </Card>
      </Card>
    </>
  );
};

export default SectionOne;
