import React from "react";
import { Card } from "../../UI/Card.styled";
import { ImageCard } from "../../UI/ImageCard.styled";
import { UiContext } from "../../../App";
import { useContext } from "react";
import { useState } from "react";
import { useReducer } from "react";
// const modePropsReducer = (state, action) => {
//   if (action.index === 1) {
//     state[1].active = true;
//   }
// };
const DropDown = ({ setMode, mode }) => {
  const Ui = useContext(UiContext);

  const clickHandler = (index) => {
    setMode((prev) => {
      const b = [...prev];
      for (let i = 0; i < b.length; i++) {
        b[i].active = false;
      }
      console.log(b);
      const a = prev[index];
      if (a.active === true) {
        a.active = false;
      } else if (a.active === false) {
        a.active = true;
      }
      b.splice(index, 1, a);
      return [...b];
    });
  };

  const rendered = mode.map((item, index) => {
    // console.log(item.active);
    return (
      <ImageCard
        key={item.id}
        // bd={"1px solid black"}
        br={"0.3rem"}
        height={"2rem"}
        src={item.src}
        hvbg={"red"}
        onClick={() => clickHandler(index)}
      />
    );
  });
  const modeRender = mode.map((item) => {
    if (item.active) {
      console.log(item.color);
      return item.color;
    }
  });
  const shadowRender = mode.map((item) => {
    if (item.active) {
      console.log(item.color);
      return item.bs;
    }
  });
  const borderRender = mode.map((item) => {
    if (item.active) {
      console.log(item.color);
      return item.bd;
    }
  });
  return (
    <>
      <Card
        ps={"absolute"}
        pslf={"32rem"}
        pstp={"3rem"}
        dp={"grid"}
        gridC={"auto auto"}
        gridjc={"space-around"}
        pd={"1rem"}
        zindex={"200"}
        height={"4rem"}
        width={"10rem"}
        bg={modeRender}
        bd={borderRender}
        br={"0.5rem"}
        // hvbg={"red"}
        // bs={"0.1px 0.1px 15px rgb(210, 210, 210)"}
        bs={shadowRender}
      >
        {rendered}
      </Card>
    </>
  );
};

export default DropDown;
