import NAVBAR from "./NavBar.module.css";
import NavCard from "./Components/NavCard";
import NavCardDropDown from "./Components/NavCardDropDown";
import ArrowUp from "../images/icon-arrow-up.svg";
import ArrowDown from "../images/icon-arrow-up.svg";
const NavBar = () => {
  const NavBarItems = [
    {
      content: "Snap",
    },
    {
      content: "Features",
      icon: ArrowDown,
      dropdown: [
        {
          text: "TodoList",
          imgsrc: "bbb",
        },
        {
          text: "Calender",
          imgsrc: "bbb",
        },
        {
          text: "Reminder",
          imgsrc: "bbb",
        },
        {
          text: "Reminder",
          imgsrc: "bbb",
        },
      ],
    },
    {
      content: "Company",
      icon: ArrowDown,
      dropdown: [
        {
          text: "History",
        },
        {
          text: "Our Team",
        },
        {
          text: "Blog",
        },
      ],
    },
    {
      content: "Careers",
    },
    {
      content: "About",
    },
    {
      content: "Login",
    },
    {
      content: "Register",
    },
  ];
  const renderedItems = NavBarItems.map((items) => {
    if (items.dropdown) {
      return <NavCardDropDown senditems={items} />;
    } else {
      return <NavCard />;
    }
    return;
  });
  return <div className={NAVBAR.container}>{renderedItems}</div>;
};
export default NavBar;
