import "./App.css";
import { useState } from "react";
import { Card } from "./components/UI/Card.styled";
import React from "react";
import HomePage from "./components/HOMEPAGE/HomePage";

import userIcon from "./images/user-regular.svg";
import moonIcon from "./images/moon-solid.svg";
import download from "./images/download.svg";

import Engineer from "./images/engineer.svg";
const App = () => {
  // const [state, setState] = useState(0);
  // setTimeout(() => {
  //   setState(state + 1);
  //   console.log(state);
  // }, 2000);
  const [mode, setMode] = useState(false);

  const UI = {
    userIcon: userIcon,
    moonIcon: moonIcon,
    Engineer: Engineer,
    download: download,
  };
  const colors = {
    bgColor: mode ? "black" : "white",
    lightGrey: " #5b5b5b",
    lighterGrey: "#484848",
    textWhite: "#ffffff",
    darkModeColor: "#0c0c0d",
    mainColor: "#ffc200",
    borderColor: "#f2f2f1",
    mainYellow: "#ffc200",
    buttonColor: "#474559",
  };
  const fontSizes = {
    NavBar: "0.7rem",
    textNormal: "0.7rem",
    textButton: "0.6rem",
  };
  return <HomePage fontSizes={fontSizes} UI={UI} colors={colors} />;
};

export default App;
