import React from "react";
import { useState } from "react";
import { Card } from "../../Card.styled";
import { ImageCard } from "../../ImageCard.styled";
const Grid1 = ({ content }) => {
  const [arrowHoverMargin, setArrowHoverMargin] = useState("0rem 1rem");
  return (
    <Card bd={"1px solid red"}>
      <Card mg={"5% 0"} height={"5%"} bd={"1px solid black"}>
        <ImageCard
          // bg={"red"}
          // va={"sub"}
          height={"100%"}
          mg={"0rem 0.5rem"}
          dp={"inline"}
          src={content.SectionOneContents[0]}
        />

        <Card
          // height={"80%"}
          // bd={"1px solid red"}
          // mg={"10rem 0rem"}
          fs={"0.5rem"}
          ps={"relative"}
          pstp={"-0.3rem"}
          dp={"inline"}
        >
          {content.SectionOneContents[1]}
        </Card>
      </Card>

      <Card fs={"2rem"} mg={"5% 0"} height={"35%"} bd={"1px solid black"}>
        {content.SectionOneContents[2]} <b>{content.SectionOneContents[3]}</b>
        &nbsp;
        {content.SectionOneContents[4]} <br />
        <b>{content.SectionOneContents[5]}</b>
      </Card>
      <Card fs={"0.7rem"} mg={"5% 0"} height={"14%"} bd={"1px solid black"}>
        {content.SectionOneContents[6]}
        <b> {content.SectionOneContents[7]}</b>
      </Card>
      {/* ----------------------------------------- */}
      <Card
        onMouseOver={() => {
          setArrowHoverMargin("0rem 1.5rem");
        }}
        onMouseOut={() => {
          setArrowHoverMargin("0rem 1rem");
        }}
        ovfx={"hidden"}
        mg={"5% 0"}
        height={"10%"}
        // bd={"1px solid black"}
      >
        <b>{content.SectionOneContents[8]}</b>
        <ImageCard
          tr={"0.4s"}
          mg={arrowHoverMargin}
          src={content.SectionOneContents[9]}
        />
        <Card ps={"relative"} pslf={"50%"} bdbm={"2px solid black"} />
      </Card>

      {/* --------------------------------------------- */}
      <Card
        mg={"15% 0rem 0rem 0rem"}
        height={"7%"}
        bd={"1px solid black"}
      ></Card>
    </Card>
  );
};

export default Grid1;
