import React from "react";
import { Card } from "./Card.styled";
import { ImageCard } from "./ImageCard.styled";
import { UiContext } from "../../App";
import { useContext } from "react";
const DropDown = () => {
  const Ui = useContext(UiContext);
  return (
    <>
      <Card
        ps={"absolute"}
        pslf={"28rem"}
        pstp={"4rem"}
        dp={"grid"}
        gridC={"auto auto"}
        gridjc={"space-around"}
        pd={"1rem"}
        zindex={"200"}
        height={"4rem"}
        width={"10rem"}
        bg={"white"}
        br={"0.5rem"}
        bs={"0.1px 0.1px 15px rgb(210, 210, 210)"}
      >
        <ImageCard
          bd={"1px solid blue"}
          br={"1rem"}
          height={"2rem"}
          src={Ui.lightModeIcon}
        />
        <ImageCard height={"2rem"} src={Ui.darkModeIcon} />
      </Card>
    </>
  );
};

export default DropDown;
