import React from "react";
import styled from "styled-components";
import Dropdown from "../../../NAVBAR/Components/Dropdown";
import NavCardDropDown from "../../../NAVBAR/Components/NavCardDropDown";
// IMAGES
import Calender from "../../../../images/icon-calendar.svg";
import TodoList from "../../../../images/icon-todo.svg";
import Reminder from "../../../../images/icon-reminders.svg";
import Planning from "../../../../images/icon-planning.svg";

import ArrowUp from "../../../../images/icon-arrow-up.svg";
import ArrowDown from "../../../../images/icon-arrow-down.svg";

const Container = (props) => {
  const Container = styled.div`
    background-color: white;
    position: relative;
    left: 35%;
    width: 65%;
    height: 100%;
  `;
  const Image = styled.img`
    margin: 1.5rem 80%;
    // border: 1px solid black;
  `;

  const NavBarItems = [
    {
      content: "snap",
      snap: "bb",
    },
    {
      drop1: 1,
      content: "Features",
      icon: ArrowUp,
      activeIcon: ArrowDown,
      dropdown: [
        {
          text: "TodoList",
          imgsrc: TodoList,
        },
        {
          text: "Calender",
          imgsrc: Calender,
        },
        {
          text: "Reminder",
          imgsrc: Reminder,
        },
        {
          text: "Planning",
          imgsrc: Planning,
        },
      ],
    },
    {
      drop2: 2,
      content: "Company",
      icon: ArrowUp,
      activeIcon: ArrowDown,
      dropdown: [
        {
          text: "History",
        },
        {
          text: "Our Team",
        },
        {
          text: "Blog",
        },
      ],
    },
    {
      content: "Careers",
    },
    {
      content: "About",
    },
    {
      content: ".",
    },
    {
      content: "Login",
    },
    {
      content: "Register",
      button: " e",
    },
  ];
  const renderedItems = NavBarItems.map((items) => {
    if (items.dropdown && !items.button) {
      return (
        <div>
          <Dropdown
            sendstyle={"block"}
            sendposition={"relative"}
            senditems={items}
          ></Dropdown>
        </div>
      );
    }
  });

  return (
    <>
      <Container>
        <Image src={props.CloseBtn} />
        <Drop>{renderedItems}</Drop>
      </Container>
    </>
  );
};

export default Container;
