import React from "react";
import { Card } from "../../UI/Card.styled";
import { ImageCard } from "../../UI/ImageCard.styled";
import { useContext, useState } from "react";
import { UiContext } from "../../../App";
const NavBar = ({ fontSizes, UI, colors }) => {
  const navList = ["About", "Projects", "Resume", "Contact"];
  const renderedNavList = navList.map((index) => {
    return (
      <Card fw={"700"} cl={colors.lighterGrey} fs={fontSizes.NavBar}>
        {index}
      </Card>
    );
  });
  return (
    <Card
      mg={"1rem 7.5%"}
      height={"2rem"}
      width={"85%"}
      bd={"0px solid red"}
      gridC={"28% 35% 37%"}
      dp={"grid"}
      //   ovfx={"hidden"}
    >
      <Card bd={"0px solid red"}>
        <ImageCard
          src={UI.userIcon}
          heigth={"1.3rem"}
          width={"1.3rem"}
          mg={"0rem 0rem -0.8rem 0rem"}
        />
        <Card
          fs={fontSizes.NavBar}
          fw={"700"}
          ps={"relative"}
          pstp={"0.7rem"}
          mg={"0rem 0.5rem"}
          dp={"inline"}
        >
          Paul
        </Card>
      </Card>
      <Card>
        <Card
          mg={"1rem 0 0 0"}
          dp={"grid"}
          gridC={"auto auto auto auto"}
          bd={"0px solid red"}
        >
          {renderedNavList}
        </Card>
      </Card>
      <Card height={"2rem"} width={"100%"} bd={"1px solid red"}>
        <Card bd={"1px solid blue"} float={"right"} height={"100%"}>
          <ImageCard
            dp={"inline"}
            height={"1.3rem"}
            width={"1.3rem"}
            src={UI.moonIcon}
            mg={"0rem 0.1rem -0.9rem 0rem"}
          />
          {/* <ImageCard src={UI.toggleOn} /> */}
          {/* <Card
            ps={"relative"}
            pstp={"0.7rem"}
            mg={"0rem 0.2rem"}
            fs={fontSizes.NavBar}
            dp={"inline"}
          >
            Color mode
          </Card> */}
        </Card>
      </Card>
    </Card>
  );
};

export default NavBar;
