import React from "react";
import { Card } from "../../UI/Card.styled";
import { ImageCard } from "../../UI/ImageCard.styled";
import { UiContext } from "../../../App";
import { useContext } from "react";
import { useState } from "react";
import { useReducer } from "react";
const modePropsReducer = (state, action) => {
  if (action.index === 1) {
    state.active = true;
  }
};
const DropDown = () => {
  const Ui = useContext(UiContext);

  // const dummyProps = [
  //   { id: 0, src: Ui.lightModeIcon, active: false },
  //   { id: 1, src: Ui.darkModeIcon, active: false },
  // ];
  const [modeProps, setModeProps] = useReducer(modePropsReducer, [
    { id: 0, src: Ui.lightModeIcon, active: false },
    { id: 1, src: Ui.darkModeIcon, active: false },
  ]);

  const clickHandler = (index) => {
    setModeProps({ index: index });
  };
  const rendered = modeProps.map((item, index) => {
    // console.log(item.active);
    return (
      <ImageCard
        key={item.id}
        bd={item.active ? "2px solid #555" : "null"}
        br={"0.3rem"}
        height={"2rem"}
        src={item.src}
        hvbg={"red"}
        onClick={() => clickHandler(index)}
      />
    );
  });
  console.log(modeProps);
  // console.log(dummyProps);
  return (
    <>
      <Card
        ps={"absolute"}
        pslf={"32rem"}
        pstp={"4rem"}
        dp={"grid"}
        gridC={"auto auto"}
        gridjc={"space-around"}
        pd={"1rem"}
        zindex={"200"}
        height={"4rem"}
        width={"10rem"}
        bg={"white"}
        br={"0.5rem"}
        // hvbg={"red"}
        bs={"0.1px 0.1px 15px rgb(210, 210, 210)"}
      >
        {rendered}
      </Card>
    </>
  );
};

export default DropDown;
