import React from "react";
import { Card } from "./Card.styled";
import { ImageCard } from "./ImageCard.styled";
import { UiContext } from "../../App";
import { useContext } from "react";
import { useState } from "react";
const DropDown = () => {
  const Ui = useContext(UiContext);
  const dummyProps = [
    { id: 1, src: Ui.lightModeIcon, active: 2 },
    { id: 2, src: Ui.darkModeIcon, active: 2 },
  ];
  const [modeProps, setModeProps] = useState(dummyProps);
  const rendered = modeProps.map((item) => {
    console.log(item.active);
    return (
      <ImageCard
        bd={item.active ? "2px solid #555" : "null"}
        br={"0.3rem"}
        height={"2rem"}
        src={item.src}
      />
    );
  });
  return (
    <>
      <Card
        ps={"absolute"}
        pslf={"32rem"}
        pstp={"4rem"}
        dp={"grid"}
        gridC={"auto auto"}
        gridjc={"space-around"}
        pd={"1rem"}
        zindex={"200"}
        height={"4rem"}
        width={"10rem"}
        bg={"white"}
        br={"0.5rem"}
        bs={"0.1px 0.1px 15px rgb(210, 210, 210)"}
      >
        {rendered}
      </Card>
    </>
  );
};

export default DropDown;
