import React from "react";
import styled from "styled-components";

const Button = (props) => {
  const Button = styled.button`
    background-color: ${props.bgcolor};
    color: ${props.color};
    margin: 2rem auto;
    text-align: center;
    border: none;
    border-radius: 1rem;
    font-size: ${props.fontsize};
  `;
  return (
    <>
      <center>
        <Button>{props.content}</Button>
      </center>
    </>
  );
};

export default Button;
