import LANDINGPAGE from "./LandingPage.module.css";
import NavBar from "./NAVBAR/NavBar";
import Content from "./CONTENT/Content";
import WhiteSpace from "./WHITESPACE/WhiteSpace";
import { useMediaQuery } from "react-responsive";
import { useEffect, useState } from "react";
import styled from "styled-components";
import Dropdown from "./MOBILE/NAVBAR/Dropdown";
import Snap from "./MOBILE/NAVBAR/Snap";
import WhiteSpace2 from "./MOBILE/NAVBAR/WhiteSpace";
import OpenMenu from "../images/icon-menu.svg";
import CloseMenu from "../images/icon-close-menu.svg";
import HeroImage from "../images/image-hero-mobile.png";
const LandingPage = () => {
  // --------- MEDIA QUERIES -------------
  const isDesktopOrLaptop = useMediaQuery({
    query: "(min-width: 700px)",
  });
  const isPhone = useMediaQuery({ query: "(min-width: 200px)" });
  const isBigScreen = useMediaQuery({ query: "(min-width: 1824px)" });
  const isTabletOrMobile = useMediaQuery({ query: "(max-width: 1224px)" });
  const isPortrait = useMediaQuery({ query: "(orientation: portrait)" });
  const isRetina = useMediaQuery({ query: "(min-resolution: 2dppx)" });
  if (isDesktopOrLaptop) {
    return (
      <div className={LANDINGPAGE.container}>
        <NavBar />
        <Content />
        <WhiteSpace />
      </div>
    );
  }
  if (isPhone) {
    const NavBar = styled.div`
      height: 5rem;
      border: 1px solid black;
      margin-top: 0.6rem;
      display: grid;
      grid-template-columns: 25% 60% 15%;
    `;

    const NavBarItems = {
      one: "snap",
      two: OpenMenu,
    };

    const Content = styled.div`
      border: 1px solid black;
      height: 25rem;
    `;
    return (
      <>
        <NavBar>
          <Snap content={NavBarItems.one}></Snap>
          <WhiteSpace2></WhiteSpace2>
          <Dropdown content={NavBarItems.two}></Dropdown>
        </NavBar>
        <Content></Content>
      </>
    );
  }
};
export default LandingPage;
